import React from "react";
import { ordinals } from "../data";
import { diff } from "deep-object-diff";

const getIntervalSymbol = (interval, intervalType) => {
  switch (intervalType) {
    case "inclusive":
      return ` //${interval}`;
    case "exclusive":
      return ` /${interval}`;
    case "none":
      return "";
    default:
      return `${interval}`;
  }
};

const getOrdinalSymbol = sortOrder => {
  return ordinals[sortOrder] + ".";
};

const groupWorksets = worksets => {
  let groupedWorksets = [];
  worksets.forEach((thisWorkset, i, arr) => {
    const prevWorkset = arr[i - 1];
    if (i === 0) {
      groupedWorksets.push([thisWorkset]);
    } else if (areTheseThingsEqual([thisWorkset, prevWorkset])) {
      groupedWorksets[groupedWorksets.length - 1].push(thisWorkset);
    } else {
      groupedWorksets.push([thisWorkset]);
    }
  });
  return groupedWorksets;
};

const areTheseThingsEqual = things => {
  const unorderedThings = things.map(thing => ({ ...thing, sortOrder: 0 }));
  const thingDiffs = unorderedThings.map((thing, i, arr) =>
    diff(arr[0], thing)
  );
  const areThingsEqual = thingDiffs.every(
    thingDiff =>
      Object.keys(thingDiff).length === 0 && thingDiff.constructor === Object
  );
  return areThingsEqual;
};

//#region Workset Components

const SingleWorksetLabelWithExercise = ({ workset }) => {
  return (
    <div>
      {workset.exercise.name}, 1x{workset.reps} @{" "}
      {workset.intensity + workset.intensityUnit}{" "}
      {getIntervalSymbol(workset.interval, workset.intervalType)}
    </div>
  );
};

const MultipleWorksetsWithoutExerciseLabel = ({ worksets }) => {
  const worksetCount = worksets.length;
  const workset = worksets[0];
  return (
    <div>
      {worksetCount}x{workset.reps} @{" "}
      {workset.intensity + workset.intensityUnit}{" "}
      {getIntervalSymbol(workset.interval, workset.intervalType)}
    </div>
  );
};

const MultipleWorksetsWithExerciseLabel = ({ worksets }) => {
  const worksetCount = worksets.length;
  const workset = worksets[0];
  return (
    <div>
      {workset.exercise.name}, {worksetCount}x{workset.reps} @{" "}
      {workset.intensity + workset.intensityUnit}{" "}
      {getIntervalSymbol(workset.interval, workset.intervalType)}
    </div>
  );
};
//#endregion

//region Round Components
const SingleRoundSingleModeWorkgroupLabel = ({ round }) => {
  const worksets = round.worksets;
  const worksetsAreEqual = areTheseThingsEqual(worksets);
  if (worksetsAreEqual) {
    return <MultipleWorksetsWithExerciseLabel worksets={worksets} />;
  } else {
    const exerciseName = worksets[0].exercise.name;
    const groupedWorksets = groupWorksets(worksets);
    return (
      <div>
        {exerciseName + ", "}
        {groupedWorksets.map(worksetGroup => {
          return (
            <MultipleWorksetsWithoutExerciseLabel worksets={worksetGroup} />
          );
        })}
      </div>
    );
  }
};

const MultipleRoundsSingleModeWorkgroupLabel = ({ workgroup }) => {
  const { rounds } = workgroup;
  const roundCount = rounds.length;
  const round = rounds[0];
  const worksets = round.worksets;
  const { exercise } = worksets[0];
  const worksetsAreEqual = areTheseThingsEqual(worksets);
  if (worksetsAreEqual) {
    return (
      <div>
        {exercise.name + ", "}
        {roundCount + " rounds"}
        <MultipleWorksetsWithoutExerciseLabel worksets={worksets} />
      </div>
    );
  } else {
    const groupedWorksets = groupWorksets(worksets);
    if (worksets.length === 1) {
      return (
        <div>
          {exercise.name + ", "}
          {roundCount + " rounds"}
          <MultipleWorksetsWithoutExerciseLabel worksets={worksets} />
        </div>
      );
    } else {
      return (
        <div>
          {exercise.name + ", "}
          {roundCount + " rounds"}
          {groupedWorksets.map(worksetGroup => (
            <MultipleWorksetsWithoutExerciseLabel worksets={worksetGroup} />
          ))}
        </div>
      );
    }
  }
};

const MultipleRoundsMultiModeWorkgroupLabel = ({ workgroup }) => {
  const { rounds } = workgroup;

  return (
    <div>
      {rounds.map((round, roundIndex) => {
        const groupedWorksets = groupWorksets(round.worksets);
        return (
          <div>
            {`Round ${roundIndex + 1}`}
            {getIntervalSymbol(round.interval, round.intervalType)}
            {groupedWorksets.map(worksetGroup =>
              worksetGroup.length === 1 ? (
                <div>
                  <SingleWorksetLabelWithExercise workset={worksetGroup[0]} />
                </div>
              ) : (
                <div>
                  <MultipleWorksetsWithExerciseLabel worksets={worksetGroup} />
                </div>
              )
            )}
          </div>
        );
        groupedWorksets.map(worksetGroup => {
          if (worksetGroup.length === 1) {
            return (
              <div>
                {`Round ${roundIndex + 1}`}
                {/* <SingleWorksetLabelWithExercise workset={worksetGroup[0]} /> */}
              </div>
            );
          } else {
            return (
              <div>
                {`Round ${roundIndex + 1}`}
                {/* <MultipleWorksetsWithExerciseLabel worksets={worksetGroup} /> */}
              </div>
            );
          }
        });
      })}
    </div>
  );
};
//#endregion

export const RxWorkgroupLabel = ({ workgroup }) => {
  const { rounds, note } = workgroup;
  const roundsCount = rounds.length;
  const roundsAreEqual = areTheseThingsEqual(rounds);
  const ordinalSymbol = getOrdinalSymbol(workgroup.sortOrder);
  if (roundsCount === 1) {
    return (
      <div className="mb-2">
        {note ? <div className="text-sm mt-1">{note}</div> : null}
        <div className="mr-1 flex">
          {ordinalSymbol}
          <SingleRoundSingleModeWorkgroupLabel round={rounds[0]} />
        </div>
      </div>
    );
  } else {
    if (roundsAreEqual) {
      return (
        <div className="mb-2">
          {note ? <div className="text-sm mt-1">{note}</div> : null}
          <div className="flex">
            <div className="mr-1">{ordinalSymbol}</div>
            <MultipleRoundsSingleModeWorkgroupLabel workgroup={workgroup} />
          </div>
        </div>
      );
    } else {
      return (
        <div className="mb-2">
          {note ? <div className="text-sm mt-1">{note}</div> : null}
          <div className="flex">
            <div className="mr-1">{ordinalSymbol}</div>
            <MultipleRoundsMultiModeWorkgroupLabel workgroup={workgroup} />
          </div>
        </div>
      );
    }
  }
};
